var ws;
var retries = 0;
var userID = -1;
var audioAlarm = new Audio('/audio/alarm.ogg');
var alarmSoundOn = false;
var soundLastPlayed = 0;
var soundInterval = 4000;

useWebSockets(true);
function onDocumentReady()
{
    createAlarmTableRows(100);
}
function onWebSocketOpen()
{
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    switch(msg.topic)
    {
       case 'alarmList':
            alarmScan(msg.payload);
            break;
        default:
        // code block
    }
}
function createAlarmTableRows(nrows)
{
    var alarmTable = document.getElementById('alarmTable');
    for (var irow = 0; irow < nrows; ++irow)
    {
        var row = alarmTable.insertRow(irow + 1);
        row.setAttribute("id", "alarmTable" + irow.toString());
        row.setAttribute("class", "tableText");
        var iconCell = row.insertCell(0);
        var devCell  = row.insertCell(1);
        var nameCell = row.insertCell(2);
        var propCell = row.insertCell(3);
        var attrCell = row.insertCell(4);
        var valueCell= row.insertCell(5);
        var unitCell = row.insertCell(6);
        var typeCell = row.insertCell(7);
        iconCell.setAttribute("id", "alarmTable" + irow.toString() + "_icon");
        devCell.setAttribute( "id", "alarmTable" + irow.toString() + "_dev");
        nameCell.setAttribute( "id", "alarmTable" + irow.toString() + "_name");
        propCell.setAttribute( "id", "alarmTable" + irow.toString() + "_prop");
        attrCell.setAttribute("id", "alarmTable" + irow.toString() + "_attr");
        valueCell.setAttribute("id", "alarmTable" + irow.toString() + "_value");
        unitCell.setAttribute("id", "alarmTable" + irow.toString() + "_unit");
        typeCell.setAttribute("id", "alarmTable" + irow.toString() + "_type");
        $('#alarmTable' + irow.toString()).hide();
        iconCell.innerHTML = "<img id='alarmTable" + irow.toString() + "_led'" + " src='/img/redLight.png' height='50px'/>";
    }

}
function alarmScan(alarmList)
{
    var deviceInfo = [];
    var nrows = $('#alarmTable tr').length;
    nrows = nrows - 1;
    if (alarmList.length > nrows) alert('Too many alarms to display!');
    for (var idev = 0; idev < alarmList.length; ++idev)
    {
        $('#alarmTable' + idev.toString()).show();
        $('#alarmTable' + idev.toString() + '_dev').html(alarmList[idev].deviceType);
        $('#alarmTable' + idev.toString() + '_name').html(alarmList[idev].name);
        $('#alarmTable' + idev.toString() + '_prop').html(alarmList[idev].prop);
        $('#alarmTable' + idev.toString() + '_attr').html(alarmList[idev].attr);
        $('#alarmTable' + idev.toString() + '_value').html(alarmList[idev].value);
        $('#alarmTable' + idev.toString() + '_unit').html(alarmList[idev].unit);
        var type = '';
        if (alarmList[idev].alarm == 1) type = 'LOW';
        if (alarmList[idev].alarm == 2) type = 'HIGH';
        if (alarmList[idev].alarm == 3) type = 'LOLO';
        if (alarmList[idev].alarm == 4) type = 'HIHI';
        $('#alarmTable' + idev.toString() + '_type').html(type);
        if (alarmList[idev].alarm < 3)
        {
            $('#alarmTable' + idev.toString() + '_led').attr('src','/img/yellowLight.png');
        }
        else
        {
            $('#alarmTable' + idev.toString() + '_led').attr('src','/img/redLight.png');
            if (alarmSoundOn)
            {
                if ((new Date().getTime() - soundLastPlayed) > soundInterval)
                {
                    audioAlarm.play();
                    soundLastPlayed = new Date().getTime();
                }
            }

        }

    }
    for (var idev = alarmList.length; idev < nrows; ++idev)
    {
        $('#alarmTable' + idev.toString()).hide();
    }

}
function toggleSound()
{
    alarmSoundOn = !alarmSoundOn;
    var soundButtonText = 'Turn Sound On';
    if (alarmSoundOn) soundButtonText = 'Turn Sound Off';
    $('#toggleSoundButton').html(soundButtonText);
}

func:
function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var oldAlarmList = context.get('oldAlarmList')
if (oldAlarmList == undefined) oldAlarmList = [];
var numDevices = objectLength(msg.payload);
var props = ['readings', 'settings'];
var alarmList = [];
var alarmCount = 0;
for (var idev = 0; idev < numDevices; ++idev)
{
    var device = msg.payload[idev];
    var keys = Object.keys(device);
    for (var ii = 0; ii < keys.length; ++ii)
    {
        if (device[keys[ii]].hasOwnProperty('type'))
        {
            if (device[keys[ii]].type == 'scalar')
            {
                if (device[keys[ii]].alarm.value > 0)
                {
                    alarmList[alarmCount] = 
                    {
                        deviceType: device.deviceType,
                        name  : device.name,
                        prop  : device[keys[ii]].action,
                        attr  : keys[ii],
                        value : device[keys[ii]].value,
                        unit  : device[keys[ii]].unit,
                        alarm : device[keys[ii]].alarm.value,
                        id    : device.deviceType + '-' +  device.name + '-' +  device[keys[ii]].action + '-' + keys[ii],
                        notify: device[keys[ii]].alarm.notify
                    };
                    ++alarmCount;
                }
            }
        }
    }
}

var newAlarmList = [];
var newAlarmCount = 0;
for (var ialarm = 0; ialarm < alarmCount; ++ialarm)
{
    var oldAlarm = oldAlarmList.find(oldAlarm => oldAlarm.id === alarmList[ialarm].id);
    if (oldAlarm == undefined) 
    {
        newAlarmList[newAlarmCount] = alarmList[ialarm];
        ++newAlarmCount;
    }
}
context.set('oldAlarmList', alarmList);
return [{topic:'alarmList', payload:{topic:'alarmList',payload:alarmList}},{topic:'newAlarmList', payload:newAlarmList}];

if (msg.payload.notify.name.length < 1) return null;
if (msg.payload.notify.number.length < 1) return null;
var type = '';
if (msg.payload.alarm == 1) type = 'LOW';
if (msg.payload.alarm == 2) type = 'HIGH';
if (msg.payload.alarm == 3) type = 'LOLO';
if (msg.payload.alarm == 4) type = 'HIHI';
var message = 'Alarm-' + type + ': ' + msg.payload.id;
message = message + ' is at ' + msg.payload.value.toString() + ' ' + msg.payload.unit
return {topic:'sms',to:msg.payload.notify.number, payload:message, from:'46000000000'};

if ('NULL' == global.get('settingsPassword'))
    return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];
if (msg.payload.payload.loginPassword != global.get('settingsPassword'))
{
    return [{topic:'getJwtToken', payload:{topic:'getJwtToken', userID:msg.payload.userID, token: null}}, null];
}
return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];

if (Number(msg.token) == msg.userID)
{
    return [msg, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Success!', userID : msg.userID}}]
}
return [null, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Bad token', userID : msg.userID}}];

