        var timeKey = splitString[ii].substring(0,splitString[ii].indexOf(':'));
        if (timeKey.indexOf(' ') == 0) timeKey = timeKey.substring(1);
        var timeData = splitString[ii].substring(splitString[ii].indexOf(':') + 1);
        if (timeData.indexOf(' ') == 0) timeData = timeData.substring(1);
        timeKey = timeKey.replace(/\s/g,'');
        timeInfo[timeKey] = timeData;
    }
}
var systemClockSynchronized = true;
if (timeInfo['Systemclocksynchronized'] == 'no') systemClockSynchronized = false;
global.set('systemClockSynchronized', systemClockSynchronized);
return {topic:'timeInfo',payload:timeInfo};

