        });
 }
function onWebSocketClose()
{

}

function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getServerTime':
                var serverTimeDate = new Date(msg.serverTime);
                $('#serverTime').val(serverTimeDate.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
