}
function setServerTime()
{
    var month = ['JAN', 'FEB', 'MAR', 'APR', 'MAY', 'JUN', 'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
    var serverTime = new Date($( "#serverTime" ).val());
    var hoursString = serverTime.getHours().toString();
    if (serverTime.getHours() < 10) hoursString = '0' + hoursString;
    var minsString = serverTime.getMinutes().toString();
    if (serverTime.getMinutes() < 10) minsString = '0' + minsString;
    var dayString = (serverTime.getDay() + 1).toString();
    if (serverTime.getDay() < 9) dayString = '0' + dayString;
    var dateString =  dayString + ' ' + month[serverTime.getMonth()]  + ' ' + serverTime.getFullYear().toString();
    dateString = dateString + ' ' + hoursString + ':' + minsString + ':00';

    dateString = 'sudo date -s "' + dateString + '"';
    sendWebSocketMessageObject(
        {
            topic       : 'setServerTime',
            userID      : userID,
            serverTime   : dateString,
        });

}

func:
function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic : msg.device.deviceType,
        payload:{
            topic           : 'getArchiveValues',
            payload         : {device: msg.device, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
for (var ii = 0; ii < numRecords; ++ii)
{
    trace[ii] = {timeStamp: records[ii].timeStamp};
    for (var ij = 0; ij < msg.device.attr.length; ++ij)
    {
        trace[ii][msg.device.attr[ij]] = records[ii][msg.device.attr[ij]];
    }
}

return {
    topic : msg.device.deviceType,
    payload:{
        topic           : 'getArchiveValues',
        payload         : {device: msg.device, trace: trace},
        userID          : msg.userID,
    }
};

var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
        deviceType                  :   1,  
        name                        :   1,  
        timeStamp                   :   1 
    } 
};
for (var ii = 0; ii < msg.payload.device.attr.length; ++ii)
{
    projectionFilter.projection[msg.payload.device.attr[ii]] = 1;
}
var queryFilter = 
{
    $and :
    [
        {
            deviceType: msg.payload.device.deviceType
        },
        {
            name: msg.payload.device.name
        },
        {
            timeStamp :
            {
                $gte: msg.payload.device.startDate,
                $lte: msg.payload.device.stopDate
            }
        }
    ] 

}
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.payload.userID,
    payload         : [queryFilter, projectionFilter],
    device          : msg.payload.device,
};
return newMsg;

var mqttArray = msg.topic.split('/');
var device = 
{
    project     : mqttArray[0],
    deviceType  : mqttArray[1],
    name        : mqttArray[2],
    prop        : mqttArray[3],
};
return {topic : msg.topic, mqttDevice : device, payload:msg.payload};

return {
    topic   :msg.payload.name, 
    payload : 
    [
        {
            $and   :    
            [
                {
                    deviceType : msg.payload.deviceType
                },
                {
                    name : msg.payload.name
                }
            ]
        }, 
        {
            projection  :
            {
                _id :   0

            }
        }
    ],
    'device'    :   msg.payload
};

if (msg.device.imageData.value == null) return;
//msg.device.timeStamp = new Date().getTime();
msg.device.lastArchive = msg.device.timeStamp;

var archiveDevice = JSON.parse(JSON.stringify(msg.device));
var updateDevice  = JSON.parse(JSON.stringify(msg.device));
delete archiveDevice.arcPeriod;
delete archiveDevice.lastArchive;
keys = Object.keys(archiveDevice);
for (var ii = 0; ii < keys.length; ++ii)
{
    if (archiveDevice[keys[ii]].hasOwnProperty('type'))
    {

        archiveDevice[keys[ii]] = JSON.parse(JSON.stringify(msg.device[keys[ii]].value));
    }
}
archiveMsg = {topic:'archiveData',payload: archiveDevice};
if (archiveDevice.archiveData < 1) archiveMsg = null;
var updateDeviceMsg = 
{
    topic:'updateDevice',
    payload:
    [
        {
            $and   :    
            [
                {
                    deviceType : msg.device.deviceType
                },
                {
                    name : msg.device.name
                }
            ]
        }, 
        updateDevice
    ]
};

return [updateDeviceMsg,archiveMsg];

if (msg.payload.topic != 'getLastImage') return null;
return {
    topic   :msg.payload.restfulDevice.deviceType + '-' + msg.payload.restfulDevice.name, 
    payload : 
    [
        {
            $and   :    
            [
                {
                    deviceType : msg.payload.restfulDevice.deviceType
                },
                {
                    name : msg.payload.restfulDevice.name
                }
            ]
        }, 
        {
            projection  :
            {
                _id :   0

            }
        }
    ],
    'device'    :   msg.payload
};

