template:
// Global Javascript
        var plotConfig = 
        {
            trace1Title : 'Temp (C)',
            trace1Color : '#557a95',
            trace1GridColor : '#beceda',
            trace2Title : 'Distance (cm)',
            trace2Color : '#f2a31f',
            plotLength  : 600000,
            width   : '',
            height  :   '500',
            htmlId  :   'historyStripChart'
        };
        let historyStripChart = new StripChart(plotConfig);
        var restfulDevice = 
        {
            deviceType  : '{{{payload.script.deviceType}}}',
            name        : '{{{payload.script.name}}}',
            project     : '{{{payload.script.project}}}'
        };
        var mqttDevice = null;
        var device = null;
        var colorScale = true;
        var enhancementLevel = 0;
        var pgm = null;
        var programStarted = false;
        var audioAlarm = new Audio('/audio/hold-your-horses.ogg');
        var audioAlarmOn = true;
        var imageDelayChanged = false;
        var ultraSoundRangeChanged = false;
        var archiveMinTempChanged = false;
        var archivePixelPercentChanged = false;

        useWebSockets(true);
        function onDocumentReady()
        {
            $('#imageDelayButtonId').hide();
            $('#ultraSoundRangeButtonId').hide();
            $('#archiveMinTempButtonId').hide();
            $('#archivePixelPercentButtonId').hide();
        }
        function onWebSocketOpen()
        {
            if (!programStarted)
            {
                programStarted = true;
                sendWebSocketMessageObject({topic : 'getLastImage', userID : userID, 'restfulDevice': restfulDevice});
            }
        }
        function onWebSocketClose()
        {
//            wsConnectC();
        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    if (msg.mqttDevice.name == restfulDevice.name)
                    {
                        heartbeat();
                        device = msg.payload;
                        mqttDevice = msg.mqttDevice;
                        updateDmaReadings();

                    }
                    break;
                case 'lastImage':
                    if (msg.userID == userID)
                    {
                        device = msg.payload;
                        if (device.imageData.value != null)
                        {
                            $('#snapshotTimeStamp').text(new Date(device.timeStamp).toLocaleString('en-SE', { timeZone: 'Europe/Stockholm' }));
                            pgm = JSON.parse(JSON.stringify(device.imageData.value));
                            $('#sensorName').text('Sensor ' + device.name + ' @ ' + device.pixelMax.value.toString() + ' C');
                            plotImage();
                            updatePixelMaxBarChart();
                        }
                    }
                    break;
                case 'getJwtToken':
                    if (msg.userID == userID) loadJwtToken(msg);
                    break;
                case 'settingAcknowledge':
                    if (msg.userID == userID)
                    {
                        if (msg.payload != 'Success!') acknowledgeDialog('Acknowledge', 'Setting', msg.payload);
                    }
                    break;
                default:
                // code block
            }
        }
// Update Readings Javascript
        function updateDmaReadings()
        {
            if (!imageDelayChanged)$('#imageDelayInputId').val(device.imageDelay.value);
            if (!ultraSoundRangeChanged)$('#ultraSoundRangeInputId').val(device.ultraSoundRange.value);
            if (!archiveMinTempChanged)$('#archiveMinTempInputId').val(device.archiveMinTemp.value);
            if (!archivePixelPercentChanged)$('#archivePixelPercentInputId').val(device.archivePixelPercent.value);
            $('#linkQualityId').text(device.linkQuality.value);
            $('#signalLevelId').text(device.signalLevel.value);
            if (device.imageData.value != null)
            {
                historyStripChart.add3TracePoints(device.pixelMax.value, device.pixelMin.value, device.ultraSoundDistance.value);
                $('#snapshotTimeStamp').text(new Date(device.timeStamp).toLocaleString('en-SE', { timeZone: 'Europe/Stockholm' }));
                $('#sensorName').text('Sensor ' + device.name + ' @ ' + device.pixelMax.value.toString() + ' C');
                pgm = JSON.parse(JSON.stringify(device.imageData.value));
                plotImage();
                updatePixelMaxBarChart();
                var image1dArray = [];
                var ipixel = 0;
                var plow = (pgm.minval - 27315) / 100;
                var phigh = (pgm.maxval - 27315) / 100;
                if ((device.pixelMax.alarm.value == 2) || (device.pixelMax.alarm.value == 4))
                {
                    if (audioAlarmOn)
                    {
                        var playPromise = audioAlarm.play();
                        if (playPromise !== undefined) 
                        {
                            playPromise.then(_ => 
                            {
                            // Automatic playback started!
                            // Show playing UI.
                            }).catch(error => 
                            {
                            // Auto-play was prevented
                            // Show paused UI.
                            });
                         }
                    }
                }
                for (var irow = 0; irow < pgm.nrows; ++irow)
                {
                    for (var icol = 0; icol < pgm.ncols; ++icol)
                    {
                        image1dArray[ipixel] = (pgm.imageArray[irow][icol] / 256.0) * (phigh - plow) + plow;
                         ++ipixel;
                    }
                }

                var layout = {title: "Temperature (C)", xaxis: {title: "Temperature (C)"}, yaxis: {title: "Count"}};

                Plotly.newPlot('pixelHistogramChart', [{x: image1dArray, type:'histogram',  autobinx: true }]);
//              Plotly.newPlot('pixelHistogramChart', [{x: image1dArray, type:'histogram',  xbins: {size: 0.5,start: 0} }],layout);
            }
            else
            {
                historyStripChart.add1TracePoints(device.ultraSoundDistance.value);
             }

            var buttonColor = 'green';
            var buttonTextColor = 'black';
            var buttonText = "Off";
            if (device.flipHorizontal.value > 0) 
            {
                buttonColor = 'red';
                buttonText = 'On';
            }
            $('#flipHorizontalId').css('color', buttonTextColor); 
            $('#flipHorizontalId').css('background-color', buttonColor); 
            $('#flipHorizontalId').text(buttonText); 

            buttonColor = 'green';
            buttonTextColor = 'black';
            buttonText = "Off";
            if (device.flipVertical.value > 0) 
            {
                buttonColor = 'red';
                buttonText = 'On';
            }
            $('#flipVerticalId').css('color', buttonTextColor); 
            $('#flipVerticalId').css('background-color', buttonColor); 
            $('#flipVerticalId').text(buttonText); 
        }
        function plotImage()
        {
            var rgb = [];
            var plow = (pgm.minval - 27315) / 100;
            var phigh = (pgm.maxval - 27315) / 100;
            var pdelta = phigh - plow;
            for (var ibox = 0; ibox < 9; ++ibox)
            {
                var intensity = ibox / 8.0;
                var pbox = Math.round(((phigh - plow) * intensity + plow) * 10.0) / 10.0;
                $('#box' + (ibox + 1).toString() + 'Label').text(pbox.toString());
                if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                if (colorScale)
                {
                    rgb = getRGB(intensity);
                }
                else
                {
                    rgb[0] = Math.floor(255 * intensity);
                    rgb[1] = Math.floor(255 * intensity);
                    rgb[2] = Math.floor(255 * intensity);
                }
                var style='width:88px;height:50px;background-color:' + 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
                $('#box' + (ibox + 1).toString()).attr('style',style);
            }
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");
            var tipPointer = document.getElementById("tipPointer");
            var tipPointerContext = tipPointer.getContext("2d");
            var tip = document.getElementById("tip");
            var tipContext = tip.getContext("2d");
            tipContext.font = "30px Arial";
            tipContext.fillStyle = "cyan";
            tipPointerContext.fillStyle = "cyan";
            if (colorScale) tipContext.fillStyle = "black";
            if (colorScale) tipPointerContext.fillStyle = "black";
            tipContext.textAlign = "left";

            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            var rectWidth  = canvasWidth  / pgm.ncols;
            var rectHeight = canvasHeight / pgm.nrows;
            canvas.onmousemove = function (e) 
            {
                var icol = Math.floor(e.layerX / rectWidth);
                if (icol < 0) icol = 0;
                if (icol >= pgm.ncols) icol = pgm.ncols - 1;
                var irow = Math.floor((canvasHeight - e.layerY) / rectHeight);
                if (irow < 0) irow = 0;
                if (irow >= pgm.nrows) irow = pgm.nrows - 1;
                var temp = Math.floor(100.0 * (pdelta * pgm.imageArray[irow][icol] / 256.0 + plow)) / 100;

                tipPointer.style.top = (e.layerY  - tipPointer.height / 2).toString()  + "px";
                tipPointer.style.left = (e.layerX - tipPointer.width / 2).toString() + "px";
                tipPointerContext.clearRect(0, 0, tipPointer.width, tipPointer.height);
                tipPointerContext.fillRect( 0, 0, tipPointer.width, tipPointer.height);

                tip.style.top = (e.layerY - tip.height - tipPointer.height).toString()  + "px";
                tip.style.left = (e.layerX).toString() + "px";
                tipContext.clearRect(0, 0, tip.width, tip.height);
                tipContext.fillText(temp.toString() + ' C', 0, 30);
            };

            for (var irow = 0; irow < pgm.nrows; ++irow)
            {
                for (var icol = 0; icol < pgm.ncols; ++icol)
                {
                    var intensity = pgm.imageArray[irow][icol] / 256.0;
                    if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                    if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                    if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                    if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                    if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                    if (colorScale)
                    {
                        rgb = getRGB(intensity);
                        rgb[0] = Math.floor(rgb[0]);
                        rgb[1] = Math.floor(rgb[1]);
                        rgb[2] = Math.floor(rgb[2]);
                    }
                    else
                    {
                        rgb[0] = Math.floor(255 * intensity);
                        rgb[1] = Math.floor(255 * intensity);
                        rgb[2] = Math.floor(255 * intensity);
                    }
                    canvasContext.fillStyle = 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
                    if (intensity > 0.98) 
                    {
                        canvasContext.fillStyle='magenta';
                        if (colorScale) canvasContext.fillStyle='white';
                    }

                    canvasContext.fillRect(icol * rectWidth, canvasHeight - rectHeight - irow * rectHeight, rectWidth, rectHeight);
                }    
            }
        }
        function mouseLeftCanvas()
        {
            var tip = document.getElementById("tip");
            var tipContext = tip.getContext("2d");
            tipContext.clearRect(0, 0, tip.width, tip.height);
            var tipPointer = document.getElementById("tipPointer");
            var tipPointerContext = tipPointer.getContext("2d");
            tipPointerContext.clearRect(0, 0, tipPointer.width, tipPointer.height);
        }
        function updatePixelMaxBarChart()
        {
            $('#pixelMaxBarPlot').show();
            var pixelMaxBarConfig = 
                {
                    lolo            : device.pixelMax.alarm.limits.lolo, 
                    low             : device.pixelMax.alarm.limits.low, 
                    high            : device.pixelMax.alarm.limits.high, 
                    hihi            : device.pixelMax.alarm.limits.hihi, 
                    value           : device.pixelMax.value, 
                    unit            : device.pixelMax.unit, 
                    textsize        : 38,
                    label           : 'pixelMax', 
                    lowColor        : '#0000ff', 
                    mediumColor     : '#00ff00',
                    highColor       : '#ff0000', 
                    backgroundColor : '#9eb5c7', 
                    labelColor      : '#000000', 
                    gridColor       : '#ffffff',
                    height          : 200, 
                    htmlId          : 'pixelMaxBarPlot'
                };
            setHorzBarWidget(pixelMaxBarConfig);
        }
        function getRGB(intensity)
        {
          var rgb = [0,0,0];
          if ( (0 <= intensity) && (intensity <= 0.25) )
          {
            rgb[0] = 0;
            rgb[1] = Math.floor(255 * intensity * 4);    
            rgb[2] = 255;
          }
          if ( (0.25 < intensity) && (intensity <= 0.50) )
          {
            rgb[0] = 0;
            rgb[1] = 255;    
            rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
          }
          if ( (0.50 < intensity) && (intensity <= 0.75) )
          {
            rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
            rgb[1] = 255;    
            rgb[2] = 0;
          }
          if ( (0.75 < intensity) && (intensity <= 1.0) )
          {
            rgb[0] = 255;
            rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
            rgb[2] = 0;
          }
          return rgb;
        }
        function toggleColorScale()
        {
            colorScale = !colorScale;
            if (colorScale)
            {
                $('#colorScaleButton').text('Color');
            }
            else
            {
                $('#colorScaleButton').text('Gray');
            }
            plotImage();
        }
        function toggleEnhancedScale(deltaEnhanced)
        {
            enhancementLevel = enhancementLevel + deltaEnhanced;
            if (enhancementLevel >  2) enhancementLevel = 2;
            if (enhancementLevel < -2) enhancementLevel = -2;
            $('#enhancementLevel').text(enhancementLevel.toString());
            plotImage();
        }
        function clearImage()
        {
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");
            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            canvasContext.clearRect(0, 0, canvasWidth, canvasHeight);        
            $('#snapshotTimeStamp').text('-');
            for (var ibox = 0; ibox < 9; ++ibox)
            {
                var intensity = ibox / 8.0;
                var pbox = 0.0;
                $('#box' + (ibox + 1).toString() + 'Label').text(pbox.toString());
            } 
            $('#pixelMaxBarPlot').hide();
        }
        function toggleSound()
        {
            audioAlarmOn = !audioAlarmOn;
            if(audioAlarmOn)
            {
                $('#soundButtonId').css('color', 'black'); 
                $('#soundButtonId').css('background-color', 'green'); 
                $('#soundButtonId').text('On'); 
            }
            else
            {
                $('#soundButtonId').css('color', 'black'); 
                $('#soundButtonId').css('background-color', 'grey'); 
                $('#soundButtonId').text('Off'); 
            }

        }
        function flipHorizontal()
        {
            if (device.flipHorizontal.value > 0)
            {
                device.flipHorizontal.value = 0;
            }
            else
            {
                device.flipHorizontal.value = 1;

            }
            $('#flipHorizontalId').css('color', 'black'); 
            $('#flipHorizontalId').css('background-color', 'grey'); 
            $('#flipHorizontalId').text('?'); 
            var mqttMessage = 
            {
                topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                payload :  device
            };
            checkLogin(sendSettingToMqtt, mqttMessage);
        }
        function flipVertical()
        {
            if (device.flipVertical.value > 0)
            {
                device.flipVertical.value = 0;
            }
            else
            {
                device.flipVertical.value = 1;

            }
            $('#flipVerticalId').css('color', 'black'); 
            $('#flipVerticalId').css('background-color', 'grey'); 
            $('#flipVerticalId').text('?'); 
            var mqttMessage = 
            {
                topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                payload :  device
            };
            checkLogin(sendSettingToMqtt, mqttMessage);
        }
        function setScalarValue(settingValue, attrName)
        {
            var settingAttr = device[attrName];
            if ( (settingAttr.alarm.limits.lolo <= settingValue) && (settingValue <= settingAttr.alarm.limits.hihi) )
            {
                settingAttr.value = settingValue;
                var mqttMessage = 
                {
                    topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                    payload :  device
                };
