        function editDevice(attrName)
        {
            $( "#editDeviceDialog" ).dialog( "option", "title", "Edit"); 
            editedAttribute = attrName;
            $( "#editDeviceDialogAttribute").html(attrName);

            var attr = device[attrName];
            $( "#loloAlarm").val(attr.alarm.limits.lolo);
            $( "#lowAlarm").val(attr.alarm.limits.low);
            $( "#highAlarm").val(attr.alarm.limits.high);
            $( "#hihiAlarm").val(attr.alarm.limits.hihi);
            $( "#notifyName").val(attr.alarm.notify.name);
            $( "#notifyNumber").val(attr.alarm.notify.number);

            $( "#editDeviceDialog" ).dialog( "open" );
        }
        function setDevice()
        {
            var attr = device[editedAttribute];
            if (isNaN($('#loloAlarm').val()) 
                || isNaN($('#lowAlarm').val()) 
                || isNaN($('#highAlarm').val()) 
                || isNaN($('#hihiAlarm').val()))
            {
                acknowledgeDialog('Error', 'Entry not a number!', editedAttribute);
                editedAttribute = '';
                return;
            }
            editedAttribute = '';

            attr.alarm.limits.lolo = Number($( "#loloAlarm").val());
            attr.alarm.limits.low = Number($( "#lowAlarm").val());
            attr.alarm.limits.high = Number($( "#highAlarm").val());
            attr.alarm.limits.hihi = Number($( "#hihiAlarm").val());
            attr.alarm.notify.number =  $( "#notifyNumber").val();
            attr.alarm.notify.name = $( "#notifyName").val();
            var mqttMessage = 
            {
                topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                payload :  device
            };
            checkLogin(sendSettingToMqtt, mqttMessage);
        }

