// StripChart Widget
        class StripChart
        {
           constructor(plotConfig) 
            {
                this.plotConfig = plotConfig;
                this.plotStartTime = new Date().getTime();
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : plotConfig.height,
                    width       : plotConfig.width,
                    xaxis       :
                    {
//                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                        type: 'date'
                    },
                    yaxis:
                    {
                        title           : plotConfig.trace1Title,
                        titlefont       : {color: plotConfig.trace1Color},
                        tickfont        : {color:plotConfig.trace1Color},
                        gridcolor       : plotConfig.trace1GridColor,
                        zerolinecolor   : plotConfig.trace1GridColor,
                        linecolor       : plotConfig.trace1GridColor,
//                        range: [0, 2]

                    },
                    yaxis2:
                    {
                        title           : plotConfig.trace2Title,
                        titlefont       : {color: plotConfig.trace2Color},
                        tickfont        : {color:plotConfig.trace2Color},
                        gridcolor       : plotConfig.trace2GridColor,
                        zerolinecolor   : plotConfig.trace2GridColor,
                        linecolor       : plotConfig.trace2GridColor,
                        overlaying: 'y',
                        side: 'right',
//                        range: [0, 2]
                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: 'Max Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'red',
                        size: 10,
                        symbol: 'circle'
                    }
                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'Min Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        size: 10,
                        symbol: 'square'
                    }
                };
                this.trace3 = 
                {
                    x: [],
                    y: [],
                    name: 'Distance',
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: this.plotConfig.trace2Color,
                        size: 5,
                        symbol: 'diamond'
                    }
                };
            }
            add1TracePoints(trace3Pt)
            {
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
