// Global Javascript
        var project    = '{{{payload.script.project}}}';
        var deviceType = '{{{payload.script.deviceType}}}';
        var deviceNames  = ['01', '02', '03', '04'];
        var deviceColors = ['red', 'blue', 'green', 'magenta'];
        var deviceShapes = ['circle', 'square', 'triangle', 'diamond'];
        var colorScale = true;
        var enhancementLevel = 0;
        var pgm = null;
        var programStarted = false;

        var plotConfig = 
        {
            trace1Title : 'Temperature (C)',
            trace1Color : '#557a95',
            trace1GridColor : '#beceda',
            plotLength  : 600,
            width   : '',
            height  :   '500',
            htmlId  :   'historyStripChart'
        };
        var layout =
        {
            showlegend  : true,
            legend      : { x: 0, y: 1.15},
            height      : '500',
            width       : '',
            xaxis       :
            {
                type: 'date'
            },
            yaxis:
            {
                title           : plotConfig.trace1Title,
                titlefont       : {color: plotConfig.trace1Color},
                tickfont        : {color:plotConfig.trace1Color},
                gridcolor       : plotConfig.trace1GridColor,
                zerolinecolor   : plotConfig.trace1GridColor,
                linecolor       : plotConfig.trace1GridColor,
                range           : [30,39]

            },
            margin: 
                {
                    t: 70, //top margin
//                    l: 0, //left margin
//                    r: 0, //right margin
//                    b: 20 //bottom margin
                },
        };
        var traces = [];
        for (var itrace = 0; itrace < deviceNames.length; ++itrace)
        {
            traces[itrace] =
            {
                x: [],
                y: [],
                name: deviceNames[itrace],
                yaxis: 'y1',
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: deviceColors[itrace],
                    size: 10,
                    symbol: deviceShapes[itrace]
                }        
            };
        }

        useWebSockets(true);
        function onDocumentReady()
        {
            $('#imageView').hide();
        }
        function onWebSocketOpen()
        {
            if (!programStarted)
            {
                programStarted = true;
            }
        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    if (msg.payload.imageData.value != null)
                        updateDmaReadings(msg.payload);
                    break;
                case 'image' :
                    if (msg.userID == userID)
                    {
                        $('#imageView').show();
                        $('#snapshotTimeStamp').text(new Date(msg.payload.timeStamp).toLocaleString('en-SE', { timeZone: 'Europe/Stockholm' }).replace(',', ''));
                        pgm = JSON.parse(JSON.stringify(msg.payload.imageData));
                        $('#sensorName').text('Sensor ' + msg.payload.name + ' @ ' + msg.payload.pixelMax.toString() + ' C');
                        plotImage();
                    }
                    break;
                case 'getJwtToken':
                    if (msg.userID == userID) loadJwtToken(msg);
                    break;
                default:
                // code block
            }
        }
        function updateDmaReadings(device)
        {
            if (device.imageData.value != null)
            {
                for (var itrace = 0; itrace < deviceNames.length; ++itrace)
                {
                    if (deviceNames[itrace] == device.name)
                    {
                        traces[itrace].x.push(device.timeStamp);
                        traces[itrace].y.push(device.pixelMax.value);
                    }
                }
                Plotly.newPlot(plotConfig.htmlId, traces, layout);
            }
        }
